import{_ as e,c as t,a5 as s,o}from"./chunks/framework.DoP1wijR.js";const r="/pub-blog/assets/image-20241127095335272.BfNnDY52.png",i="/pub-blog/assets/image-20241127102037596.h6WJnT9C.png",p="/pub-blog/assets/image-20241127102233557.xE0kjQfQ.png",l="/pub-blog/assets/image-20241127103001436.DxWcjtHF.png",n="/pub-blog/assets/image-20241127111351966.CW_De0IJ.png",m="/pub-blog/assets/0000000000011111111.20241108162413.21428389402407934282631586754603.j1rv_0RU.png",g="/pub-blog/assets/image-20241127103329588.BHDdSnTp.png",c="/pub-blog/assets/image-20241127103441091.D3pje6a0.png",E="/pub-blog/assets/image-20241127103512816.DOraYl3g.png",h="/pub-blog/assets/0000000000011111111.20241108162413.94137298091598438736514604862804.CWFDEGh3.png",b="/pub-blog/assets/0000000000011111111.20241108162413.37859214279609577220399148607883.DVQAmSbt.png",u="/pub-blog/assets/image-20241127103940121.DgwmzSpH.png",d="/pub-blog/assets/image-20241127104028586.BQrskgEz.png",A="/pub-blog/assets/image-20241127104100616.BlxT98of.png",_="/pub-blog/assets/image-20241127104421049.Cf2C-dPI.png",x="/pub-blog/assets/image-20241127104501011.D9SrchIu.png",y="/pub-blog/assets/image-20241127104552646.B00YOXp4.png",f="/pub-blog/assets/image-20241127104809629.CEnaXKAY.png",B="/pub-blog/assets/image-20241127104905347.BxMu0tQw.png",C="/pub-blog/assets/image-20241127105255233.CcG1RqhI.png",P="/pub-blog/assets/image-20241127105420075.FHq05XE3.png",D="/pub-blog/assets/image-20241127105504102.MiCaAARK.png",q="/pub-blog/assets/image-20241127105615225.Bfk2qs4t.png",S="/pub-blog/assets/image-20241127105819036.D3kgfTLx.png",k="/pub-blog/assets/image-20241127110100621.fQ0ibPkM.png",H="/pub-blog/assets/image-20241127110412484.Dq5jJDQU.png",O="/pub-blog/assets/image-20241127110440114.B5dMAIXx.png",N="/pub-blog/assets/image-20241127110908371.BEH6chpg.png",F="/pub-blog/assets/image-20241127110949471.kiF1M_63.png",w="/pub-blog/assets/image-20241127111044105.BsCixUJn.png",T=JSON.parse('{"title":"HarmonyOS Next 元服务新建到上架全流程","description":"","frontmatter":{},"headers":[],"relativePath":"鸿蒙开发技巧/HarmonyOS Next 元服务新建到上架全流程/HarmonyOS Next 元服务新建到上架全流程.md","filePath":"鸿蒙开发技巧/HarmonyOS Next 元服务新建到上架全流程/HarmonyOS Next 元服务新建到上架全流程.md","lastUpdated":1734827919000}'),G={name:"鸿蒙开发技巧/HarmonyOS Next 元服务新建到上架全流程/HarmonyOS Next 元服务新建到上架全流程.md"};function I(j,a,v,Q,R,J){return o(),t("div",null,a[0]||(a[0]=[s('<h1 id="harmonyos-next-元服务新建到上架全流程" tabindex="-1">HarmonyOS Next 元服务新建到上架全流程 <a class="header-anchor" href="#harmonyos-next-元服务新建到上架全流程" aria-label="Permalink to &quot;HarmonyOS Next 元服务新建到上架全流程&quot;">​</a></h1><p>接上篇</p><p>这篇文章的主要目的是介绍元服务从新建到上家的完整流程</p><h1 id="在agc平台上新建一个项目" tabindex="-1">在AGC平台上新建一个项目 <a class="header-anchor" href="#在agc平台上新建一个项目" aria-label="Permalink to &quot;在AGC平台上新建一个项目&quot;">​</a></h1><p><a href="https://developer.huawei.com/consumer/cn/service/josp/agc/index.html#/harmonyOSDevPlatform/172249065903274453" target="_blank" rel="noreferrer">链接</a></p><p>一个项目可以多个应用</p><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124191241104.png?x-image-process=style/style-8860" alt="image-20241124191241104"></p><h1 id="agc新建一个元服务应用" tabindex="-1">AGC新建一个元服务应用 <a class="header-anchor" href="#agc新建一个元服务应用" aria-label="Permalink to &quot;AGC新建一个元服务应用&quot;">​</a></h1><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124191300505.png?x-image-process=style/style-8860" alt="image-20241124191300505"></p><h1 id="新建一个本地元服务项目" tabindex="-1">新建一个本地元服务项目 <a class="header-anchor" href="#新建一个本地元服务项目" aria-label="Permalink to &quot;新建一个本地元服务项目&quot;">​</a></h1><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124191643071.png?x-image-process=style/style-8860" alt="image-20241124191643071"></p><hr><p>如果成功在AGC平台上新建过元服务，那么这里会自动显示</p><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124191744693.png?x-image-process=style/style-8860" alt="image-20241124191744693"></p><h1 id="修改元服务名称" tabindex="-1">修改元服务名称 <a class="header-anchor" href="#修改元服务名称" aria-label="Permalink to &quot;修改元服务名称&quot;">​</a></h1><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124191327227.png?x-image-process=style/style-8860" alt="image-20241124191327227"></p><h1 id="修改元服务图标" tabindex="-1">修改元服务图标 <a class="header-anchor" href="#修改元服务图标" aria-label="Permalink to &quot;修改元服务图标&quot;">​</a></h1><p>重要，上架审核很严谨</p><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124191343134.png?x-image-process=style/style-8860" alt="image-20241124191343134"></p><ol><li><p>先自己下载随意一张图片</p></li><li><p>使用画图工具 图像属性 修改 1024px</p><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/PixPin_2024-11-24_19-15-16.gif?x-image-process=style/style-8860" alt="PixPin_2024-11-24_19-15-16"></p></li><li><p>使用开发工具中 Image Asset 来制作图片</p></li></ol><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124191814129.png?x-image-process=style/style-8860" alt="image-20241124191814129"></p><p><img src="https://wsy996.obs.cn-east-3.myhuaweicloud.com/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91/HarmonyOS%20Next%20%E7%AE%80%E5%8D%95%E4%B8%8A%E6%89%8B%E5%85%83%E6%9C%8D%E5%8A%A1%E5%BC%80%E5%8F%91.assets/image-20241124192132959.png?x-image-process=style/style-8860" alt="image-20241124192132959"></p><h1 id="发布流程" tabindex="-1">发布流程 <a class="header-anchor" href="#发布流程" aria-label="Permalink to &quot;发布流程&quot;">​</a></h1><p><img src="'+r+'" alt="image-20241127095335272"></p><h1 id="介绍签名文件" tabindex="-1">介绍签名文件 <a class="header-anchor" href="#介绍签名文件" aria-label="Permalink to &quot;介绍签名文件&quot;">​</a></h1><ul><li><strong>密钥</strong>：包含非对称加密中使用的公钥和私钥，存储在密钥库文件中，格式为**.p12**，公钥和私钥对用于数字签名和验证。</li><li><strong>证书请求文件</strong>：格式为**.csr**，全称为Certificate Signing Request，包含密钥对中的公钥和公共名称、组织名称、组织单位等信息，用于向AppGallery Connect申请数字证书。</li><li><strong>数字证书</strong>：格式为**.cer**，由华为AppGallery Connect颁发。</li><li><strong>Profile文件</strong>：格式为**.p7b**，包含HarmonyOS应用/元服务的包名、数字证书信息、描述应用/元服务允许申请的证书权限列表，以及允许应用/元服务调试的设备列表（如果应用/元服务类型为Release类型，则设备列表为空）等内容，每个应用/元服务包中均必须包含一个Profile文件。</li></ul><p>其中，多个元服务可以共用、<strong>.p12</strong>、<strong>.csr</strong>、<strong>.cer</strong>。也就是说**.p7b** 需要每一个项目单独生成一次</p><h1 id="生成密钥和证书请求文件" tabindex="-1">生成密钥和证书请求文件 <a class="header-anchor" href="#生成密钥和证书请求文件" aria-label="Permalink to &quot;生成密钥和证书请求文件&quot;">​</a></h1><blockquote><p>此操作会得到两个文件</p></blockquote><p><img src="'+i+'" alt="image-20241127102037596"></p><hr><p><img src="'+p+'" alt="image-20241127102233557"></p><hr><p><img src="'+l+'" alt="image-20241127103001436"></p><h1 id="申请发布证书和profile文件" tabindex="-1">申请发布证书和Profile文件 <a class="header-anchor" href="#申请发布证书和profile文件" aria-label="Permalink to &quot;申请发布证书和Profile文件&quot;">​</a></h1><blockquote><p>此操作也会得到两个文件</p></blockquote><h2 id="申请发布证书步骤如下" tabindex="-1">申请发布证书步骤如下： <a class="header-anchor" href="#申请发布证书步骤如下" aria-label="Permalink to &quot;申请发布证书步骤如下：&quot;">​</a></h2><ol><li><p>登录<a href="https://developer.huawei.com/consumer/cn/service/josp/agc/index.html#/" target="_blank" rel="noreferrer">AppGallery Connect</a>，选择**“证书、APP ID和Profile”**。</p><p><img src="'+n+'" alt="image-20241127111351966"></p></li><li><p>在左侧导航栏选择“<strong>证书、APP ID和Profile &gt; 证书</strong>”，进入“证书”页面，点击“<strong>新增证书</strong>”。</p><p><img src="'+m+'" alt="img"></p></li><li><p>在弹出的“新增证书”窗口填写要申请的证书信息，点击“提交”。</p><p><img src="'+g+'" alt="image-20241127103329588"></p></li><li><p>下载 cer</p><p><img src="'+c+'" alt="image-20241127103441091"></p></li><li><p><strong>得到发布证书</strong></p></li></ol><p><img src="'+E+'" alt="image-20241127103512816"></p><h2 id="申请发布profile" tabindex="-1">申请发布Profile <a class="header-anchor" href="#申请发布profile" aria-label="Permalink to &quot;申请发布Profile&quot;">​</a></h2><p>Profile格式为.p7b，包含HarmonyOS应用/元服务的包名、数字证书信息、HarmonyOS应用/元服务允许申请的证书权限列表，以及允许应用/元服务调试的设备列表（如果应用/元服务类型为Release类型，则设备列表为空）等内容。每个HarmonyOS应用/元服务包中均必须包含一个Profile文件</p><p>申请发布Profile步骤如下：</p><ol><li><p>登录 AppGallery Connect，选择<strong>证书、APP ID和Profile</strong></p><p><img src="'+h+'" alt="img"></p></li><li><p>在左侧导航栏选择**“证书、APP ID和Profile &gt; Profile”<strong>，进入“<strong>Profile</strong>”页面，点击右上角</strong>“添加”**。</p><p><img src="'+b+'" alt="img"></p></li><li><p>在“<strong>添加Profile</strong>”页面，填写<strong>Profile</strong>信息，完成后点击“添加”。</p><p><img src="'+u+'" alt="image-20241127103940121"></p></li><li><p>下载 Profile</p><p><img src="'+d+'" alt="image-20241127104028586"></p></li><li><p>得到ProFile文件</p><p><img src="'+A+'" alt="image-20241127104100616"></p></li></ol><h1 id="手动签名" tabindex="-1">手动签名 <a class="header-anchor" href="#手动签名" aria-label="Permalink to &quot;手动签名&quot;">​</a></h1><p>让你的元服务使用刚才的证书进行手动签名</p><p><img src="'+_+'" alt="image-20241127104421049"></p><h1 id="打包构建" tabindex="-1">打包构建 <a class="header-anchor" href="#打包构建" aria-label="Permalink to &quot;打包构建&quot;">​</a></h1><p><img src="'+x+'" alt="image-20241127104501011"></p><p>得到APP文件</p><p><img src="'+y+'" alt="image-20241127104552646"></p><h1 id="新建发布" tabindex="-1">新建发布 <a class="header-anchor" href="#新建发布" aria-label="Permalink to &quot;新建发布&quot;">​</a></h1><p>回到AGC平台，新建发布</p><p><img src="'+f+'" alt="image-20241127104809629"></p><h1 id="编辑发布资料" tabindex="-1">编辑发布资料 <a class="header-anchor" href="#编辑发布资料" aria-label="Permalink to &quot;编辑发布资料&quot;">​</a></h1><p><img src="'+B+'" alt="image-20241127104905347"></p><hr><p><img src="'+C+'" alt="image-20241127105255233"></p><hr><p><img src="'+P+'" alt="image-20241127105420075"></p><h1 id="备案域名" tabindex="-1">备案域名 <a class="header-anchor" href="#备案域名" aria-label="Permalink to &quot;备案域名&quot;">​</a></h1><p>此时，如果你的应用没有备案，是会被拒绝的。这里的备案是说，你既要有一个备案过的域名+备案的元服务</p><p>一个根域名可以对应多个元服务。如</p><p>a.baidu.com 元服务A</p><p>b.baidu.com 元服务B</p><p><img src="'+D+'" alt="image-20241127105504102"></p><p>备案对于新手来说也是一个大的步骤，所以真想要发布一个应用的话，尽量先备案</p><p><a href="https://juejin.cn/post/6844903840165134344#heading-5" target="_blank" rel="noreferrer">腾讯云参考链接：</a></p><p><img src="'+q+'" alt="image-20241127105615225"></p><hr><p>这一步执行完毕，你会得到一个自己的<a href="https://console.cloud.tencent.com/domain/all-domain/all" target="_blank" rel="noreferrer">备案过的域名</a></p><p><img src="'+S+'" alt="image-20241127105819036"></p><h1 id="备案元服务" tabindex="-1">备案元服务 <a class="header-anchor" href="#备案元服务" aria-label="Permalink to &quot;备案元服务&quot;">​</a></h1><p>备案网站、应用、元服务都是一样的流程</p><p><a href="https://console.cloud.tencent.com/beian/manage" target="_blank" rel="noreferrer">备案元服务</a></p><p><img src="'+k+'" alt="image-20241127110100621"></p><hr><p>填写备案信息</p><p><img src="'+H+'" alt="image-20241127110412484"></p><h1 id="审核备案" tabindex="-1">审核备案 <a class="header-anchor" href="#审核备案" aria-label="Permalink to &quot;审核备案&quot;">​</a></h1><p><img src="'+O+'" alt="image-20241127110440114"></p><ol><li><p>当你填写完毕后，腾讯云会给你电话，如果你信息填写不对，他们会协助你修改的。一般当天会来电话。</p></li><li><p>腾讯云审核通过了，会进入到<strong>管局审核阶段</strong>，可能会来电话，也可能不来。都是通过了的话，你手机会受到一条短信。必须当天把短信中的验证码填写的<a href="https://beian.miit.gov.cn/#/Integrated/index" target="_blank" rel="noreferrer">管局备案系统</a>上。此时，备案流程结束</p><p><img src="'+N+'" alt="image-20241127110908371"></p></li></ol><p><img src="'+F+'" alt="image-20241127110949471"></p><h1 id="agc重新提交审核" tabindex="-1">AGC重新提交审核 <a class="header-anchor" href="#agc重新提交审核" aria-label="Permalink to &quot;AGC重新提交审核&quot;">​</a></h1><p><img src="'+w+'" alt="image-20241127111044105"></p><h1 id="后续" tabindex="-1">后续 <a class="header-anchor" href="#后续" aria-label="Permalink to &quot;后续&quot;">​</a></h1><p>后续继续关注AGC平台信息即可</p>',86)]))}const U=e(G,[["render",I]]);export{T as __pageData,U as default};
