import{_ as a,c as i,a5 as o,o as r}from"./chunks/framework.DoP1wijR.js";const l="/pub-blog/assets/image-20241219092436235.DDqaOWQt.png",t="/pub-blog/assets/image-20241219093119586.qpjjoMHn.png",d="/pub-blog/assets/image-20241219093722816.LVwtuSp6.png",u="/pub-blog/assets/image-20241219093840013.CLVlEV9X.png",p="/pub-blog/assets/image-20241219093925639.DHiCoUFx.png",n="/pub-blog/assets/image-20241219094156393.CUDDsdoW.png",s="/pub-blog/assets/image-20241219094225699.oYLvUu9e.png",h="/pub-blog/assets/image-20241219094414865.CPABkZmF.png",k=JSON.parse('{"title":"uniapp极速上手鸿蒙开发","description":"","frontmatter":{},"headers":[],"relativePath":"鸿蒙开发技巧/uniapp极速上手鸿蒙开发/uniapp极速上手鸿蒙开发.md","filePath":"鸿蒙开发技巧/uniapp极速上手鸿蒙开发/uniapp极速上手鸿蒙开发.md","lastUpdated":1734830878000}'),c={name:"鸿蒙开发技巧/uniapp极速上手鸿蒙开发/uniapp极速上手鸿蒙开发.md"};function b(m,e,g,v,f,_){return r(),i("div",null,e[0]||(e[0]=[o('<h1 id="uniapp极速上手鸿蒙开发" tabindex="-1">uniapp极速上手鸿蒙开发 <a class="header-anchor" href="#uniapp极速上手鸿蒙开发" aria-label="Permalink to &quot;uniapp极速上手鸿蒙开发&quot;">​</a></h1><p>uniapp 团队 与版本 <code>4.28.2024092502</code> 起，支持鸿蒙应用开发，现在是 <code>4.36.2024112817</code>,同时支持鸿蒙应用和元服务开发了。</p><p>我们现在上手感受一下</p><h2 id="环境配置" tabindex="-1">环境配置 <a class="header-anchor" href="#环境配置" aria-label="Permalink to &quot;环境配置&quot;">​</a></h2><ul><li>HBuilderX 4.24+ <a href="https://www.dcloud.io/hbuilderx.html" target="_blank" rel="noreferrer">下载地址</a></li><li><a href="https://developer.huawei.com/consumer/cn/download/" target="_blank" rel="noreferrer">DevEco Studio</a><ul><li>HBuilderX 4.24+ 要求 DevEco-Studio 5.0.3.400+，HBuilderX 4.31+ 要求 DevEco-Studio 5.0.3.800+。</li><li>鸿蒙系统版本 API 12 以上 （DevEco-Studio有内置鸿蒙模拟器）</li></ul></li><li><strong>HBuilderX 4.31+ 构建的鸿蒙运行包不支持 x86_64 平台，会影响到 Windows 系统和部分 Mac 系统的鸿蒙模拟器无法使用，需使用真机调试</strong></li></ul><p><strong>底下主要针对真机做的演示</strong></p><h2 id="项目要求" tabindex="-1">项目要求 <a class="header-anchor" href="#项目要求" aria-label="Permalink to &quot;项目要求&quot;">​</a></h2><p>在创建uniapp项目的时候，需要选择 vue3，vue2不支持</p><h2 id="搭建流程" tabindex="-1">搭建流程 <a class="header-anchor" href="#搭建流程" aria-label="Permalink to &quot;搭建流程&quot;">​</a></h2><ol><li>AGC平台上新建项目，目的是活动boundleName以及调试和发布证书</li><li>下载和安装 DevEco Studio</li><li>使用 <strong>DevEco Studio</strong> 创建项目，然后配置boundleName和调试和发布证书</li><li>复制证书相关的配置</li><li>下载和安装 hbuilder</li><li>hbuilder 下载相关插件</li><li>hbuilder 配置 DevEco Studio 工具路径</li><li>hbuilder新建uniapp vue3工程</li><li>hbuilder配置鸿蒙应用的证书</li><li>hbuilder运行项目</li></ol><h2 id="agc平台上新建项目" tabindex="-1">AGC平台上新建项目 <a class="header-anchor" href="#agc平台上新建项目" aria-label="Permalink to &quot;AGC平台上新建项目&quot;">​</a></h2><p>可以根据需求选择新建工程还是<a href="https://developer.huawei.com/consumer/cn/doc/app/agc-help-createproject-0000001100334664" target="_blank" rel="noreferrer">项目</a>。 这里选择项目</p><h2 id="下载和安装-deveco-studio" tabindex="-1">下载和安装 DevEco Studio <a class="header-anchor" href="#下载和安装-deveco-studio" aria-label="Permalink to &quot;下载和安装 DevEco Studio&quot;">​</a></h2><p>下载和安装<a href="https://developer.huawei.com/consumer/cn/doc/harmonyos-guides-V5/ide-software-install-V5" target="_blank" rel="noreferrer">DevEco Studio</a></p><h2 id="boundlename和调试和发布证书" tabindex="-1">boundleName和调试和发布证书 <a class="header-anchor" href="#boundlename和调试和发布证书" aria-label="Permalink to &quot;boundleName和调试和发布证书&quot;">​</a></h2><p>因为真机在调试时候需要使用调试证书</p><p>应用在发布的时候需要使用发布整数，因此一次性都配置获得即可</p><p><a href="https://developer.huawei.com/consumer/cn/doc/harmonyos-guides-V5/application-dev-overview-V5#section42841246144813" target="_blank" rel="noreferrer">配置链接</a></p><p><img src="'+l+'" alt="image-20241219092436235"></p><h2 id="deveco-studio-新建项目获得证书配置信息" tabindex="-1">DevEco Studio 新建项目获得证书配置信息 <a class="header-anchor" href="#deveco-studio-新建项目获得证书配置信息" aria-label="Permalink to &quot;DevEco Studio 新建项目获得证书配置信息&quot;">​</a></h2><p>这个步骤主要为了得到证书的配置代码，uniapp运行项目的时候需要用到</p><p>在使用DevEco Studio新建完项目后，参考链接 <a href="https://developer.huawei.com/consumer/cn/doc/harmonyos-guides-V5/ide-signing-V5#section297715173233" target="_blank" rel="noreferrer">进行证书的配置</a></p><p>得到配置文件 <code>build-profile.json5</code> 后续复制整个代码到uniapp创建的项目即可</p><p><img src="'+t+'" alt="image-20241219093119586"></p><h2 id="下载和安装-hbuilder" tabindex="-1">下载和安装 hbuilder <a class="header-anchor" href="#下载和安装-hbuilder" aria-label="Permalink to &quot;下载和安装 hbuilder&quot;">​</a></h2><p><a href="https://www.dcloud.io/hbuilderx.html" target="_blank" rel="noreferrer">这里下载和安装</a></p><h2 id="hbuilder-下载相关插件" tabindex="-1">hbuilder 下载相关插件 <a class="header-anchor" href="#hbuilder-下载相关插件" aria-label="Permalink to &quot;hbuilder 下载相关插件&quot;">​</a></h2><p><code>工具-插件安装</code> 关键是这几个 <code>鸿蒙、vue3</code></p><p><img src="'+d+'" alt="image-20241219093722816"></p><h2 id="hbuilder-配置-deveco-studio-工具路径" tabindex="-1">hbuilder 配置 DevEco Studio 工具路径 <a class="header-anchor" href="#hbuilder-配置-deveco-studio-工具路径" aria-label="Permalink to &quot;hbuilder 配置  DevEco Studio 工具路径&quot;">​</a></h2><p>这里配置DevEco Studio 工具的路径 <code>工具-设置</code></p><p><img src="'+u+'" alt="image-20241219093840013"></p><h2 id="hbuilder新建uniapp-vue3工程" tabindex="-1">hbuilder新建uniapp vue3工程 <a class="header-anchor" href="#hbuilder新建uniapp-vue3工程" aria-label="Permalink to &quot;hbuilder新建uniapp vue3工程&quot;">​</a></h2><p>新建vue3工程</p><p><img src="'+p+'" alt="image-20241219093925639"></p><h2 id="hbuilder配置鸿蒙应用的证书" tabindex="-1">hbuilder配置鸿蒙应用的证书 <a class="header-anchor" href="#hbuilder配置鸿蒙应用的证书" aria-label="Permalink to &quot;hbuilder配置鸿蒙应用的证书&quot;">​</a></h2><p>在项目根目录下配置 <code>\\harmony-configs\\build-profile.json5</code> 如果不存在，则手动新建。</p><p>然后复制、粘贴证书代码进去</p><p><img src="'+n+'" alt="image-20241219094156393"></p><h2 id="hbuilder运行项目" tabindex="-1">hbuilder运行项目 <a class="header-anchor" href="#hbuilder运行项目" aria-label="Permalink to &quot;hbuilder运行项目&quot;">​</a></h2><p>最后，运行项目</p><p><img src="'+s+'" alt="image-20241219094225699"></p><h2 id="效果" tabindex="-1">效果 <a class="header-anchor" href="#效果" aria-label="Permalink to &quot;效果&quot;">​</a></h2><p><img src="'+h+'" alt="image-20241219094414865"></p>',44)]))}const x=a(c,[["render",b]]);export{k as __pageData,x as default};
